<?php

/**
 * Convert a decimal number to binary format.
 * 
 * @param int $decimal The decimal number to convert.
 * @return string The converted binary representation of the decimal number.
 */
function decimalToBinary($decimal) {
    // Check if the input is an integer
    if (!is_int($decimal)) {
        return "Invalid input";
    }

    // Convert the decimal number to a binary string
    $binary = decbin($decimal);

    // Add the format characters 'db' at the beginning and end of the string
    $formattedBinary = "db" . $binary . "db";

    return $formattedBinary;
}
