// Function to check if a number is prime
function isPrime($n) {
    if ($n <= 1) {
        return false;
    }

    for ($i = 2; $i * $i <= $n; $i++) {
        if ($n % $i == 0) {
            return false;
        }
    }

    return true;
}

// Function to calculate the sum of digits of a number
function digitSum($number) {
    $sum = 0;

    while ($number > 0) {
        $sum += $number % 10; // Add the last digit
        $number = (int)($number / 10); // Remove the last digit
    }

    return $sum;
}
