/**
 * Überprüft, ob alle Schlüssel in einem Wörterbuch Klein- oder Großbuchstaben sind.
 *
 * @param array $dict Das zu überprüfende Wörterbuch.
 *
 * @return bool True, wenn alle Schlüssel Strings in Klein- oder Großbuchstaben sind, False sonst.
 */
function checkDictCase($dict){
    // Wenn das Wörterbuch leer ist, gibt es False zurück
    if (empty($dict)) {
        return false;
    }

    // Überprüfe, ob alle Schlüssel Strings sind
    foreach ($dict as $key => $value) {
        if (!is_string($key)) {
            return false; // Wenn ein Schlüssel kein String ist, gibt es False zurück
        }
    }

    // Überprüfe, ob alle Schlüssel in Klein- oder Großbuchstaben sind
    foreach ($dict as $key => $value) {
        if (ctype_upper($key)) { // Wenn ein Schlüssel nur Großbuchstaben enthält
            return false; // gibt es False zurück
        }
        if (ctype_lower($key)) { // Wenn ein Schlüssel nur Kleinbuchstaben enthält
            continue; // Weiter zum nächsten Schlüssel
        } else {
            return false; // Wenn ein Schlüssel sowohl Groß- als auch Kleinbuchstaben enthält, gibt es False zurück
        }
    }

    // Wenn alle Überprüungen erfolgreich waren, gibt es True zurück
    return true;
}
