/**
 * Gibt ein Tupel (a, b) zurück, wobei 'a' die größte negative Ganzzahl und 'b' die kleinste positive Ganzzahl in einer Liste ist.
 * Wenn es keine negativen oder positiven Ganzzahlen gibt, geben Sie sie als None zurück.
 *
 * @param array $lst Die Liste mit ganzen Zahlen
 * @return array Ein Tupel (a, b) mit der größten negativen und kleinsten positiven Ganzzahl, oder (None, None), wenn keine vorhanden sind
 */
function largestSmallestIntegers($lst){
    // Filter die Liste nach negativen und positiven Ganzzahlen
    $negatives = array_filter($lst, function($x) { return $x < 0; });
    $positives = array_filter($lst, function($x) { return $x > 0; });

    // Wenn es keine negativen oder positiven Ganzzahlen gibt, geben Sie None zurück
    if (empty($negatives) && empty($positives)) {
        return [null, null];
    }

    // Finden Sie die größte negative und kleinste positive Ganzzahl
    $largestNegative = max($negatives);
    $smallestPositive = min($positives);

    // Wenn es keine negativen oder positiven Ganzzahlen gibt, geben Sie None zurück
    if ($largestNegative === null || $smallestPositive === null) {
        return [null, null];
    }

    // Gibt das Tupel (a, b) zurück
    return [$largestNegative, $smallestPositive];
}
