/**
 * Berechnet das Produkt der ungeraden Ziffern einer positiven Ganzzahl.
 *
 * @param int $n Eine positive Ganzzahl.
 *
 * @return int Das Produkt der ungeraden Ziffern. Wenn alle Ziffern gerade sind, wird 0 zurückgegeben.
 */
function digits($n){
    // Die Zahl in eine Zeichenkette umwandeln
    $str = (string) $n;

    // Ein Array für die ungeraden Ziffern erstellen
    $ungeradeZiffern = [];

    // Jede Ziffer der Zeichenkette überprüfen und wenn sie ungerade ist, in das Array aufnehmen
    foreach (str_split($str) as $ziffer) {
        if ((int) $ziffer % 2 !== 0) {
            $ungeradeZiffern[] = (int) $ziffer;
        }
    }

    // Wenn keine ungeraden Ziffern gefunden wurden, 0 zurückgeben
    if (empty($ungeradeZiffern)) {
        return 0;
    }

    // Das Produkt der ungeraden Ziffern berechnen und zurückgeben
    $produkt = 1;
    foreach ($ungeradeZiffern as $ziffer) {
        $produkt *= $ziffer;
    }

    return $produkt;
}
