/**
 * Diese Funktion nimmt eine Liste von ganzen Zahlen entgegen und gibt die Summe der Ziffern der größten Primzahl zurück.
 *
 * @param array $lst Die Liste von ganzen Zahlen.
 *
 * @return int Die Summe der Ziffern der größten Primzahl.
 */
function skjkasdkd($lst) {
    // Funktion, um zu überprüfen, ob eine Zahl prim ist
    function isPrime($num) {
        if ($num <= 1) return false;
        for ($i = 2; $i <= sqrt($num); $i++) {
            if ($num % $i == 0) return false;
        }
        return true;
    }

    // Funktion, um die Summe der Ziffern einer Zahl zu berechnen
    function sumOfDigits($num) {
        $sum = 0;
        while ($num > 0) {
            $sum += $num % 10;
            $num = (int)($num / 10);
        }
        return $sum;
    }

    // Finden Sie die größte Primzahl in der Liste
    $maxPrime = null;
    foreach ($lst as $num) {
        if (isPrime($num)) {
            if ($maxPrime === null || $num > $maxPrime) {
                $maxPrime = $num;
            }
        }
    }

    // Wenn keine Primzahlen in der Liste gefunden wurden, gibt es keine größte Primzahl
    if ($maxPrime === null) return 0;

    // Berechnen Sie die Summe der Ziffern der größten Primzahl
    return sumOfDigits($maxPrime);
}
