/**
 * Überprüft, ob alle Schlüssel in einem Wörterbuch entweder Klein- oder Großbuchstaben sind.
 *
 * @param array $dict Das zu überprüfende Wörterbuch.
 *
 * @return bool True, wenn alle Schlüssel entweder Klein- oder Großbuchstaben sind, sonst False.
 */
function checkDictCase($dict){
    if (empty($dict)) {
        return false; // Wenn das Wörterbuch leer ist, gibt es False zurück
    }

    $allLower = array_reduce(array_keys($dict), function ($carry, $key) {
        return $carry && ctype_lower($key);
    }, true);

    $allUpper = array_reduce(array_keys($dict), function ($carry, $key) {
        return $carry && ctype_upper($key);
    }, true);

    return $allLower || $allUpper;
}
```
