/**
 * Gibt alle geraden Ziffern zwischen a und b in aufsteigender Reihenfolge zurück.
 *
 * @param int $a Die erste positive Ganzzahl.
 * @param int $b Die zweite positive Ganzzahl.
 *
 * @return array Eine Liste mit allen geraden Ziffern zwischen a und b.
 */
function generateIntegers($a, $b) {
    // Überprüfe, ob a kleiner oder gleich b ist. Wenn nicht, tausche sie aus.
    if ($a > $b) {
        list($a, $b) = array($b, $a);
    }

    // Erstelle eine Liste mit allen geraden Ziffern zwischen a und b.
    $geradeZahlen = [];
    for ($i = $a; $i <= $b; $i++) {
        if ($i % 2 === 0) {
            $geradeZahlen[] = $i;
        }
    }

    // Rückgabe der Liste mit geraden Ziffern.
    return $geradeZahlen;
}

