/**
 * Überprüft, ob alle Schlüssel in einem gegebenen Wörterbuch entweder alle Kleinbuchstaben oder alle Großbuchstaben sind.
 *
 * @param array $dict Das zu überprüfende Wörterbuch
 * @return bool True, wenn alle Schlüssel entweder alle Kleinbuchstaben oder alle Großbuchstaben sind, sonst False
 */
function checkDictCase($dict) {
    if (empty($dict)) { // Wenn das Wörterbuch leer ist, gibt es False zurück
        return false;
    }

    $allLower = true; // Annahme: Alle Schlüssel sind in Kleinbuchstaben
    $allUpper = true; // Annahme: Alle Schlüssel sind in Großbuchstaben

    foreach ($dict as $key => $value) {
        if (!is_string($key)) { // Wenn der Schlüssel kein String ist, gibt es False zurück
            return false;
        }

        if (ctype_upper($key)) { // Wenn der Schlüssel ein Großbuchstaben-String ist
            $allLower = false; // Dann kann er nicht alle Kleinbuchstaben sein
        } elseif (ctype_lower($key)) { // Wenn der Schlüssel ein Kleinbuchstaben-String ist
            $allUpper = false; // Dann kann er nicht alle Großbuchstaben sein
        }
    }

    return $allLower || $allUpper; // Gibt True zurück, wenn entweder alle Schlüssel in Kleinbuchstaben oder alle in Großbuchstaben sind
}
