/**
 * Diese Funktion nimmt einen String als Eingabe und gibt den resultierenden String zurück.
 * Wenn der String keine Buchstaben enthält, wird er umgedreht. Ansonsten werden alle Buchstaben 
 * in Groß- oder Kleinschreibung geändert.
 *
 * @param string $s Der Eingabestring
 * @return string Der resultierende String
 */
function solve($s){
    // Wenn der String leer ist, geben wir ihn zurück
    if (empty($s)) {
        return $s;
    }

    // Wir erstellen ein neues Array, in dem jeder Buchstabe in Groß- oder Kleinschreibung geändert wird
    $result = array();
    for ($i = 0; $i < strlen($s); $i++) {
        if (ctype_alpha($s[$i])) { // Wenn der Zeichen ist ein Buchstabe
            $result[] = ctype_upper($s[$i]) ? strtolower($s[$i]) : strtoupper($s[$i]);
        } else {
            $result[] = $s[$i]; // Wenn es kein Buchstabe ist, belassen wir ihn so wie er ist
        }
    }

    // Wir setzen das Array in einen String um und kehren ihn um, wenn der ursprüngliche String keine Buchstaben enthält
    if (ctype_alpha($s)) {
        return implode('', array_reverse($result));
    } else {
        return implode('', $result);
    }
}
```
