// Faktorisierungsmethode mit Primzahlen und Multiplikator
function greatestCommonDivisor($a, $b) {
    if ($a == 0) return $b;
    if ($b == 0) return $a;

    while ($a != $b) {
        if ($a > $b) {
            $a -= floor($a / floorLog2($a));
        } else {
            $b -= floor($b / floorLog2($b));
        }
    }

    return $a;
}

// Logarithmus zur Erhöhung der Geschwindigkeit 
function floorLog2($number){
    $exp = 0;

    while (pow(2, $exp) <= $number)
        $exp++;

    return $exp - 1;
}

