 /**
  * Berechnet den größten gemeinsamen Teiler von zwei ganzen Zahlen.
  *
  * @param int $a Die erste Ganzzahl.
  * @param int $b Die zweite Ganzzahl.
  *
  * @return int Der größte gemeinsame Teiler der beiden Zahlen.
  */
function greatestCommonDivisor($a, $b){
    // Verwende die Euclidischen Algorithmen zur Berechnung des GCD
    while ($b != 0) {
        // Ermittle den Rest der Division von a durch b
        $temp = $b;
        $b = $a % $b; 
        // Aktualisiere a mit dem Wert von b
        $a = $temp;
    }
    return abs($a); // GCD ist immer nicht negativ
}

