function fib($n){
    // Check if n is less than or equal to 0
    if ($n <= 0) {
        // If true, return 0 as the first Fibonacci number
        return 0;
    } elseif ($n == 1) {
        // If true, return 1 as the second Fibonacci number
        return 1;
