/**
 * Returns the number of vowels in a given string.
 * Vowels are 'a', 'e', 'i', 'o', 'u'. When 'y' is at the end, it's also considered a vowel.
 *
 * @param string $s The input string to count vowels from.
 *
 * @return int The number of vowels in the string.
 */
function vowelsCount($s){
    // Convert the string to lowercase for simplicity
    $s = strtolower($s);

    // Initialize a counter for vowels
    $vowels = 0;

    // Check if 'y' is at the end of the string and increment the vowel count if so
    if (substr($s, -1) == 'y') {
        $vowels++;
        $s = substr($s, 0, -1);
    }

    // Iterate over each character in the string
    for ($i = 0; $i < strlen($s); $i++) {
        // Check if the current character is a vowel
        if (in_array($s[$i], ['a', 'e', 'i', 'o', 'u'])) {
            $vowels++;
        }
    }

    return $vowels;
}
