            if (!isPrime($j)) continue;

            for ($k = $j + 1; $k < $a; $k++) {
                if (!isPrime($k)) continue;

                // If the product of these three numbers equals a, we return true
                if ($i * $j * $k == $a) return true;
            }
        }
    }

    // If no combination is found, we return false
    return false;
}

