 /**
  * This function takes an integer $n as input and returns the sum of its digits in binary format.
  *
  * @param int $n A positive integer with constraints 0 ≤ N ≤ 10000.
  * @return string The sum of digits in binary format.
  */
function solve($n){
    // Convert the number to binary
    $binary = decbin($n);

    // Initialize a variable to store the sum of digits
    $sum = 0;

    // Iterate over each character in the binary string
    for ($i = 0; $i < strlen($binary); $i++) {
        // Add the integer value of the current digit to the sum
        $sum += (int)$binary[$i];
    }

    // Return the sum as a string
    return strval($sum);
}

