 /**
  * Returns the 2nd smallest element of the list.
  * If there is no such element, returns null.
  *
  * @param array $lst A list of integers.
  * @return int|null The 2nd smallest element of the list, or null if it doesn't exist.
  */
 function nextSmallest($lst){
    // Check if the list has less than 2 elements
    if (count($lst) < 2) {
        return null;
    }

    // Sort the array in ascending order
    sort($lst);

    // Return the second element of the sorted array
    return $lst[1];
}

