 /**
  * Returns a list of all words from string s that contain exactly n consonants.
  *
  * @param string $s The input string containing only letters and spaces.
  * @param int $n The number of consonants the words should have.
  *
  * @return array A list of words with exactly n consonants in order they appear in the string s.
  */
function selectWords($s, $n){
    // Split the input string into an array of words
    $words = explode(' ', $s);

    // Initialize an empty array to store the result
    $result = [];

    // Iterate over each word in the array
    foreach ($words as $word) {
        // Remove spaces from the word and convert it to lower case for simplicity
        $cleanWord = strtolower(str_replace(' ', '', $word));

        // Count the number of consonants in the clean word
        $consonantCount = 0;
        for ($i = 0; $i < strlen($cleanWord); $i++) {
            if (ctype_alpha($cleanWord[$i]) && !in_array($cleanWord[$i], ['a', 'e', 'i', 'o', 'u'])) {
                $consonantCount++;
            }
        }

        // If the number of consonants in the word is equal to n, add it to the result array
        if ($consonantCount == $n) {
            $result[] = $word;
        }
    }

    // Return the result array
    return $result;
}
