 /**
  * Returns the product of the odd digits in a given positive integer n.
  * If all digits are even, returns 0.
  *
  * @param int $n A positive integer.
  * @return int The product of the odd digits or 0 if all digits are even.
  */
function digits($n){
    // Convert the number to a string to easily access each digit
    $str = (string) $n;

    // Initialize the product variable to 1
    $product = 1;

    // Iterate over each character in the string
    for ($i = 0; $i < strlen($str); $i++) {
        // Convert the character back to an integer and check if it's odd
        if ((int) $str[$i] % 2 != 0) {
            // If the digit is odd, multiply it with the product
            $product *= (int) $str[$i];
        }
    }

    // Return the product of the odd digits or 0 if all digits are even
    return $product == 1 ? 0 : $product;
}

