 /**
  * This function filters an array of numbers and returns the count of elements 
  * that are greater than 10 and both first and last digits of a number are odd (1, 3, 5, 7, 9).
  *
  * @param array $nums An array of numbers
  * @return int The count of special numbers in the array
  */
function specialfilter($nums){
    $count = 0;
    foreach ($nums as $num) {
        // Check if the number is greater than 10
        if ($num > 10) {
            // Convert the number to string to easily access first and last digits
            $strNum = (string)$num;
            // Check if both first and last digits are odd
            if ((int)$strNum[0] % 2 != 0 && (int)$strNum[strlen($strNum) - 1] % 2 != 0) {
                // If all conditions are met, increment the count
                $count++;
            }
        }
    }
    return $count;
}

