 /**
  * This function filters an array of numbers and returns the count of elements that meet two conditions:
  * 1. The number is greater than 10.
  * 2. Both first and last digits of the number are odd (1, 3, 5, 7, 9).
  *
  * @param array $nums An array of numbers
  * @return int The count of elements that meet both conditions
  */
function specialfilter($nums){
    $count = 0;
    foreach ($nums as $num) {
        if ($num > 10 && isOddDigit($num)) {
            $count++;
        }
    }
    return $count;
}

/**
 * This function checks if the first and last digits of a number are odd.
 *
 * @param int $num The input number
 * @return bool True if both first and last digits are odd, false otherwise
 */
function isOddDigit($num) {
    $str = (string)$num;
    return in_array($str[0], [1, 3, 5, 7, 9]) && in_array($str[strlen($str) - 1], [1, 3, 5, 7, 9]);
}
