 /**
  * This function calculates the sum of squares of odd numbers in a given list.
  * It ignores negative numbers and non-integer values. If the list is empty, it returns 0.
  *
  * @param array $lst A list of integers
  * @return int The sum of squares of odd numbers in the list
  */
function doubleTheDifference($lst){
    // Initialize a variable to store the sum of squares of odd numbers
    $sumOfSquares = 0;

    // Iterate over each number in the list
    foreach ($lst as $num) {
        // Check if the number is an integer and positive
        if (is_int($num) && $num > 0) {
            // Check if the number is odd
            if ($num % 2 != 0) {
                // Add the square of the number to the sum
                $sumOfSquares += pow($num, 2);
            }
        }
    }

    // Return the sum of squares
    return $sumOfSquares;
}

