 /**
  * Reverses the case of letters in a given string, while keeping non-letters as they are.
  * If the string contains no letters, it is reversed.
  *
  * @param string $s The input string
  * @return string The resulting string with case reversal and/or reversal applied
  */
 function solve($s){
    // Initialize an empty string to store the result
    $result = '';

    // Iterate over each character in the input string
    for ($i = 0; $i < strlen($s); $i++) {
        $char = $s[$i];

        // Check if the character is a letter
        if (ctype_alpha($char)) {
            // If it's an uppercase letter, convert it to lowercase and vice versa
            $result .= ctype_upper($char) ? strtolower($char) : strtoupper($char);
        } else {
            // If it's not a letter, keep it as it is
            $result .= $char;
        }
    }

    // Reverse the resulting string if it contains no letters
    if (strpos($result, '') === false) {
        return strrev($result);
    }

    // Return the resulting string with case reversal and/or reversal applied
    return $result;
}

