/**
 * Returns the MD5 hash equivalent string of the given input text.
 *
 * @param string $text The input text to be hashed.
 *
 * @return string|null The MD5 hash equivalent string if the input is not empty, otherwise null.
 */
function stringToMd5($text){
    // Check if the input text is empty
    if (empty($text)) {
        return null;
    }

    // Create a new MD5 hash object
    $md5Hash = md5($text);

    // Return the MD5 hash equivalent string
    return $md5Hash;
}

