/**
 * This function calculates the MD5 hash of a given string.
 *
 * @param string $text The input string to be hashed.
 *
 * @return string|null The MD5 hash equivalent string, or null if the input is empty.
 */
function stringToMd5($text) {
    // Check if the input string is empty
    if (empty($text)) {
        return null;
    }

    // Calculate the MD5 hash of the input string using PHP's built-in hash function
    $md5Hash = hash('md5', $text);

    // Return the calculated MD5 hash as a string
    return $md5Hash;
}

