/**
 * Überprüft, ob das Guthaben eines Bankkontos zu irgendeinem Zeitpunkt unter Null fällt.
 *
 * @param array $operations Eine Liste von Einzahlungs- und Abhebungsvorgängen auf einem Bankkonto.
 *
 * @return bool True, wenn das Guthaben des Kontos zu irgendeinem Zeitpunkt unter Null fällt, andernfalls False.
 */
function belowZero($operations) {
    $balance = 0; // Initialisierung des Kontosaldo mit 0

    foreach ($operations as $operation) {
        $balance += $operation; // Aktualisierung des Kontosaldo nach jeder Operation

        if ($balance < 0) { // Überprüfung, ob das Guthaben unter Null gefallen ist
            return true; // Wenn ja, sofortige Rückgabe von True
        }
    }

    return false; // Wenn nach allen Operationen kein Guthaben unter Null festgestellt wurde, Rückgabe von False
}
