";  // Ausgabe: 465
echo sumToN(100) . "
"; // Ausgabe: 5050
echo sumToN(5) . "
";   // Ausgabe: 15
echo sumToN(10) . "
";  // Ausgabe: 55
echo sumToN(1) . "
";   // Ausgabe: 1
