/**
 * Berechnet die Fläche eines Dreiecks anhand der Seitenlängen.
 * 
 * @param float $a Die Länge der ersten Seite.
 * @param float $b Die Länge der zweiten Seite.
 * @param float $c Die Länge der dritten Seite.
 * 
 * @return float Die Fläche des Dreiecks auf 2 Dezimalstellen gerundet, wenn die drei Seiten ein gültiges Dreieck bilden. Andernfalls -1.
 */
function triangleArea($a, $b, $c) {
    // Überprüfen, ob die drei Seiten ein gültiges Dreieck bilden
    if ($a + $b > $c && $a + $c > $b && $b + $c > $a) {
        // Berechnen des Halbumfangs
        $s = ($a + $b + $c) / 2;

        // Berechnen der Fläche mit Heron's Formel
        $area = sqrt($s * ($s - $a) * ($s - $b) * ($s - $c));

        // Runden auf 2 Dezimalstellen
        return round($area, 2);
    } else {
        // Wenn die drei Seiten kein gültiges Dreieck bilden, gib -1 zurück
        return -1;
    }
}
