/**
 * Konvertiert eine Dezimalzahl in ein Binärformat.
 * 
 * @param int $decimal Die Dezimalzahl, die konvertiert werden soll.
 * @return string Ein String, der die Binärzahl darstellt. Jedes Zeichen im String ist '0' oder '1'.
 */
function decimalToBinary($decimal) {
    // Konvertiere die Dezimalzahl in eine Binärzahl
    $binary = decbin($decimal);

    // Füge das Formatierungszeichen 'db' am Anfang und am Ende hinzu
    $formattedBinary = "db" . $binary . "db";

    return $formattedBinary;
}

// Testbeispiele
print(decimalToBinary(15)   . "
");  # returns "db1111db"
print(decimalToBinary(32)   . "
");  # returns "db100000db"
