/**
 * Überprüft, ob alle Schlüssel in einem Wörterbuch entweder Klein- oder Großbuchstaben sind.
 *
 * @param array $dict Das zu überprüfende Wörterbuch.
 *
 * @return bool True, wenn alle Schlüssel entweder Klein- oder Großbuchstaben sind, sonst False.
 */
function checkDictCase($dict) {
    // Wenn das Wörterbuch leer ist, geben wir sofort False zurück
    if (empty($dict)) {
        return false;
    }

    // Überprüfen, ob alle Schlüssel Strings sind
    foreach ($dict as $key => $value) {
        if (!is_string($key)) {
            return false; // Wenn ein Schlüssel kein String ist, geben wir False zurück
        }
    }

    // Bestimmen, ob die Schlüssel entweder alle Klein- oder alle Großbuchstaben sind
    $firstKey = key_first($dict);
    if (ctype_lower($firstKey)) {
        // Überprüfen, ob alle Schlüssel Kleinbuchstaben sind
        foreach ($dict as $key => $value) {
            if (!ctype_lower($key)) {
                return false; // Wenn ein Schlüssel nicht nur Kleinbuchstaben enthält, geben wir False zurück
            }
        }
    } elseif (ctype_upper($firstKey)) {
        // Überprüfen, ob alle Schlüssel Großbuchstaben sind
        foreach ($dict as $key => $value) {
            if (!ctype_upper($key)) {
                return false; // Wenn ein Schlüssel nicht nur Großbuchstaben enthält, geben wir False zurück
            }
        }
    } else {
        return false; // Wenn der erste Schlüssel weder Klein- noch Großbuchstaben ist, geben wir False zurück
    }

    // Wenn alle Überprüfungen bestanden wurden, geben wir True zurück
    return true;
}

// Hilfsfunktion, um den ersten Schlüssel aus einem Wörterbuch zu erhalten
function key_first($array) {
    reset($array);
    return key($array);
}
