/**
 * Diese Funktion generiert eine Liste mit n Elementen.
 * Der Wert des Elements an der Stelle i ist das Fakultät von i, wenn i gerade ist,
 * oder die Summe der Zahlen von 1 bis i, wenn i ungerade ist. 
 * Die Fakultät von i ist das Produkt der Zahlen von 1 bis i (1 * 2 * ... * i).
 *
 * @param int $n Die Anzahl der Elemente in der Liste.
 * @return array Eine Liste mit den berechneten Werten.
 */
function f($n) {
    $result = [];
    for ($i = 1; $i <= $n; $i++) {
        if ($i % 2 == 0) { // Wenn i gerade ist
            $factorial = 1;
            for ($j = 1; $j <= $i; $j++) {
                $factorial *= $j;
            }
            $result[] = $factorial;
        } else { // Wenn i ungerade ist
            $sum = 0;
            for ($k = 1; $k <= $i; $k++) {
                $sum += $k;
            }
            $result[] = $sum;
        }
    }
    return $result;
}

// Test der Funktion
print_r(f(5)); // Ausgabe: Array ( [0] => 1 [1] => 2 [2] => 6 [3] => 24 [4] => 15 )
