/**
 * Diese Funktion berechnet das Produkt der ungeraden Ziffern einer gegebenen Zahl.
 *
 * @param int $n Die Eingabezahl.
 * @return int Das Produkt der ungeraden Ziffern. Wenn alle Ziffern gerade sind, wird 0 zurückgegeben.
 */
function digits($n) {
    $product = 1;

    // Wir konvertieren die Zahl in eine Zeichenkette, um jede Ziffer einzeln zu bearbeiten
    $str = (string) $n;

    // Für jede Ziffer in der Zeichenkette
    for ($i = 0; $i < strlen($str); $i++) {
        // Wir konvertieren die Ziffer zurück in eine Ganzzahl
        $digit = (int) $str[$i];

        // Wenn die Ziffer ungerade ist, multiplizieren wir sie mit dem Produkt
        if ($digit % 2 != 0) {
            $product *= $digit;
        }
    }

    // Wenn das Produkt immer noch 1 ist, bedeutet dies, dass alle Ziffern gerade waren
    if ($product == 1) {
        return 0;
    } else {
        return $product;
    }
}

// Testfälle
echo digits(1) . "
";   // 1
echo digits(4) . "
";   // 0
echo digits(235) . "
"; // 15
