 * Gegeben eine positive ganze Zahl n, gib das Produkt der ungeraden Ziffern zurück.
 *     Gib 0 zurück, wenn alle Ziffern gerade sind.
 * 
 * @param int $n Eine positive ganze Zahl
 * @return int Das Produkt der ungeraden Ziffern
 */
function digits($n) {
    $product = 1;
    while ($n > 0) {
        $digit = $n % 10;
        if ($digit % 2 !== 0) {
            $product *= $digit;
        }
        $n = (int)($n / 10);
    }
    return $product ? $product : 0;
}

