 *
 * @param string $txt Der zu überprüfende Text.
 *
 * @return bool True, wenn das letzte Zeichen ein alphabetisches Zeichen ist und nicht Teil eines Wortes ist, False sonst.
 */
function checkIfLastCharIsALetter($txt) {
    // Überprüfen, ob der Text nicht leer ist
    if (empty($txt)) {
        return false;
    }

    // Das letzte Zeichen des Textes erhalten
    $lastChar = substr($txt, -1);

    // Überprüfen, ob das letzte Zeichen ein alphabetisches Zeichen ist
    if (!ctype_alpha($lastChar)) {
        return false;
    }

    // Den Text in Wörter aufteilen
    $words = explode(' ', $txt);

    // Das letzte Wort erhalten
    $lastWord = end($words);

