/**
 * Berechnet den speziellen Fakultät eines gegebenen Integers.
 * 
 * Der brasilianische Fakultät ist definiert als:
 *     brazilian_factorial(n) = n! * (n-1)! * (n-2)! * ... * 1!
 *     wobei n > 0
 *
 * @param int $n Der Eingabewert.
 * @return int Der spezielle Fakultät des Eingabewerts.
 */
function specialFactorial($n) {
    $result = 1;
    for ($i = $n; $i > 0; $i--) {
        $factorial = 1;
        for ($j = $i; $j > 0; $j--) {
            $factorial *= $j;
        }
        $result *= $factorial;
    }
    return $result;
}

