/**
 * Returns the product of the odd digits in a given number.
 * If all digits are even, returns 0.
 *
 * @param int $n A positive integer.
 * @return int The product of the odd digits or 0 if all digits are even.
 */
function digits($n) {
    // Initialize the product to 1
    $product = 1;

    // Convert the number to a string to easily access each digit
    $str = (string) $n;

    // Iterate over each character (digit) in the string
    for ($i = 0; $i < strlen($str); $i++) {
        // Get the current digit and convert it back to an integer
        $digit = (int) $str[$i];

        // Check if the digit is odd
        if ($digit % 2 !== 0) {
            // If the digit is odd, multiply it with the product
            $product *= $digit;
        }
    }

    // If the product is still 1, that means all digits were even, so return 0
    if ($product == 1) {
        return 0;
    }

    // Return the final product
    return $product;
}

// Test cases
