/**
 * This function calculates the sum of squares of the numbers in the list that are odd.
 * It ignores numbers that are negative or not integers.
 *
 * @param array $lst A list of numbers.
 *
 * @return int The sum of squares of the odd numbers in the list.
 */
function doubleTheDifference($lst) {
    $sum = 0;
    foreach ($lst as $num) {
        // Check if the number is an integer and not negative
        if (is_int($num) && $num >= 0) {
            // Check if the number is odd
            if ($num % 2 != 0) {
                // Add the square of the number to the sum
                $sum += pow($num, 2);
            }
        }
    }
    return $sum;
}

