    $result = '';

    // Iteriere über jedes Zeichen im Eingabestring
    for ($i = 0; $i < strlen($string); $i++) {
        // Holen Sie das aktuelle Zeichen
        $char = $string[$i];

        // Überprüfen, ob das Zeichen ein Großbuchstabe ist
        if (ctype_upper($char)) {
            // Wandeln Sie es in einen Kleinbuchstaben um
            $result .= strtolower($char);
        } elseif (ctype_lower($char)) {
            // Wandeln Sie es in einen Großbuchstaben um
            $result .= strtoupper($char);
        } else {
            // Fügen Sie das Zeichen unverändert hinzu, falls es kein Buchstabe ist
